import {BALL_CLICK, BALL_CONTINUE, BALL_CLOSE, 
	BALL_CHANGE, RUN_CHANGE, RT_CHANGE,
	REQUEST_BALLS_PENDING,REQUEST_BALLS_SUCCESS,REQUEST_BALLS_FAILED
} from './constants'

export const setBall=(value)=>{
	return({type:BALL_CLICK, payload:value});
}

export const setBallClose=(value)=>{
	return({type:BALL_CLOSE, payload:value});
}

export const setBallContinue=(value)=>{
	return({type:BALL_CONTINUE, payload:value});
}

export const setBallChange=(value)=>{
	return({type:BALL_CHANGE, payload:value});
}

export const setRTchange=(value)=>{
	return({type:RT_CHANGE, payload:value});
}

export const setRunChange=(value)=>{
	return({type:RUN_CHANGE, payload:value});
}

export const requestBalls=(dispatch)=>{
	dispatch({type:REQUEST_BALLS_PENDING});
	fetch('http://localhost:3000/balls', ).then(res => res.json())
	.then(data => dispatch({type:REQUEST_BALLS_SUCCESS, payload: data}))	
	.catch(err => dispatch({type:REQUEST_BALLS_FAILED, payload:err}));
}
