import {BALL_CLICK, BALL_CONTINUE, BALL_CLOSE, 
	BALL_CHANGE, RUN_CHANGE, RT_CHANGE,
	REQUEST_BALLS_PENDING,REQUEST_BALLS_SUCCESS,REQUEST_BALLS_FAILED
} from './constants'

const initialState={
	isPending : false,
	error : '',
	balls : [],
	runsConceeded : 0,
	ballType: 0,
	runType : 0,
	ballDisplay: "",
	show:false,
	ballId:1
}

export const requestBalls =(state = initialState, action = {}) =>{
	switch(action.type){
		case REQUEST_BALLS_PENDING:
		return Object.assign({},state, {isPending:true});

		case REQUEST_BALLS_SUCCESS:	 
		{
			console.log(action);
			return Object.assign({},state, {isPending:false, balls : action.payload});
		}

		case REQUEST_BALLS_FAILED:	 
		return Object.assign({},state, {isPending:false, error : action.payload});

		default:
		return state;
	}
}

export const setBall = (state =initialState, action = {} )=>{	
	switch(action.type){
		case BALL_CLICK:
		{
			console.log('reducer',action.payload.target.dataset);
		return Object.assign({},state, {show:action.payload});
	}

		case BALL_CLOSE:
		return Object.assign({},state, {show:action.payload});

		case BALL_CHANGE:
		return Object.assign({},state, {ballType:action.payload});

		case RT_CHANGE:
		return Object.assign({},state, {runType:action.payload});

		case RUN_CHANGE:
		return Object.assign({},state, {runsConceeded:action.payload});

		case BALL_CONTINUE:
		{
			var ballDisp = "";
			switch(state.ballType){
				case "0":
				ballDisp= state.runsConceeded+"";
				break;

				case "1":
				ballDisp= state.runsConceeded+"NB";
				break;

				case "2":
				ballDisp= state.runsConceeded+'WD';
				break;

				default :
				ballDisp= state.runsConceeded+'';
				break;
			}

			switch(state.runType){
				case "0":
				ballDisp= ballDisp+'';
				break;

				case "1":
				ballDisp= ballDisp+'B';
				break;

				case "2":
				ballDisp= ballDisp+'LB';
				break;

				case "3":
				ballDisp= ballDisp+'WK';
				break;

				default :
				ballDisp= ballDisp+'';
				break;
			}

			return Object.assign({},state, {ballDisplay:ballDisp, show:false});
		}

		default:
		return state; 
	}
}
