import React,{Component} from 'react'
import {Button} from 'react-bootstrap';
import './Ball.css';

const Ball  = ({onClick, ballDisplay, show})=>{

		return(

			<div className="dib grow br2 pa3 ma2 bw2 shadow-5">	
				<Button type="button" className="btn btn-success btn-circle btn-xl" style={{
                    transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: show ? '1' : '0'
                }}
					onClick = {onClick}>{ballDisplay} 
				</Button>				
			</div>
			);

}

export default Ball;
