import React,{Component} from 'react'
import {Button} from 'react-bootstrap';
import './Ball.css';

const Ball  = ({onClick, ballDisplay, show, ballId, ballIdChanged})=>{

		return(

			<div  style={{
                    transform: show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: show ? '1' : '0'
                }}>	
				<Button type="button" className="btn btn-success btn-circle btn-xl" 
					onClick = {onClick} id = {ballId}>
					{ballIdChanged === ballId ? {ballDisplay}:''}		
				</Button>
				<p className="tc">{'Ball:'+ballId}</p>				
			</div>
			);

}

export default Ball;
