import React, {Component} from 'react';
import './App.css';
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-balham.css';
import 'tachyons';
import {connect} from 'react-redux'

import Scroll from './components/Scroll/Scroll'
import DialogBox from './components/DialogBox/DialogBox'
import Ball from './components/Ball/Ball'

import {setBall, setBallClose, setBallChange, setRTchange,setRunChange,setBallContinue, requestBalls} 
from './actions'


const mapStateToProps=(state)=>{
  return  {
    runsConceeded : state.setBall.runsConceeded,
    ballType: state.setBall.ballType,
    runType : state.setBall.runType,
    ballDisplay: state.setBall.ballDisplay,
    show:state.setBall.show,
    balls: state.requestBalls.balls,
    isPending:state.requestBalls.isPending,
    error:state.requestBalls.error
  }
}

const mapDispatchToProps=(dispatch)=>{
  return {
    onSetBall:()=>dispatch(setBall(true)),
    onBallClose:()=>dispatch(setBallClose(false)),
    onBallChange:(event)=>dispatch(setBallChange(event.target.value)),
    onRTChange:(event)=>dispatch(setRTchange(event.target.value)),
    onRunChange:(event)=>dispatch(setRunChange(event.target.value)),
    onBallContinue:(event)=>dispatch(setBallContinue(event.target.value)),
    onRequestBalls : () => requestBalls(dispatch)
  }
}

class App extends Component{
  constructor(props) {
        super(props);
         /* 
        this.state = {
            columnDefs: [
                {headerName: 'Match Id', field: 'matchid'},
