      res => res.json()).then(data =>{
         this.state.rowData = data.slice();
         this.setState(this.state);
      })*/


      this.props.onRequestBalls();
  }    

   render(){ 

    const {show, onSetBall, onBallClose, onBallChange, onRTChange, onRunChange, onBallContinue, ballDisplay, balls} = this.props;
    return (
      <div>
        <h1 className='tc bg-red br4'>{'Scoreboard'}</h1>
        <Scroll >
        {
          balls.map((ball, i) => {

            return (
              <div key={i } className="dib grow br2 pa3 ma2 bw2 shadow-5" >
                <Ball key={i} onClick={onSetBall} ballDisplay={ballDisplay} show={ball.visible}/> 
              </div>
            );
          })
        }

          <DialogBox show = {show} close={onBallClose} ballChange = {onBallChange} rtChange = {onRTChange}
                runChange = {onRunChange} continue = {onBallContinue}/>
        {
          /*
          <div className="ag-theme-balham pa3"
                style={{height: '600px', width:'1000px', justifyContent:'center'}} >
            <AgGridReact  
                enableSorting={true}
                enableFilter={true}
                columnDefs={this.state.columnDefs}
                rowData={this.state.rowData}>
            </AgGridReact>
          </div>*/
        }
        </Scroll>
      </div>
  );
}
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
