import {BALL_CLICK, BALL_SET, BALL_CLOSE, 
	BALLTYPE_CHANGE, RUNTYPE_CHANGE, RUNS_CHANGE,
	REQUEST_BALLS_PENDING,REQUEST_BALLS_SUCCESS,REQUEST_BALLS_FAILED
} from './constants'

import update from 'react-addons-update';


const initialState ={
	isPending : false,
	error : '',
	balls : [],
	show:false,
	score:0,
	wks:0,
	currentBall:0,
	lastRT:0,
	lastBT:0,
	LastRun:0,
	lastBD:''
}

export const setBalls=(state =initialState, action = {})=>{
	switch(action.type){
		case REQUEST_BALLS_PENDING:
		return Object.assign({},state, {isPending:true});

		case REQUEST_BALLS_SUCCESS:	 
		return {...state, ispending:false, currentBall:0, balls:action.payload};

		case REQUEST_BALLS_FAILED:	 
		return Object.assign({},state, {isPending:false, error : action.payload});

		case BALL_CLICK:
		{	

			if(state.balls[parseInt(action.event)-1].ballDisplay === "" )
			{
				console.log('IF');
				return update(state, 
				{
					balls:{
					[parseInt(action.event)-1]:{	
						balldisplay:{$set:state.lastBD},
						runs:{$set:state.LastRun},	
						balltype:{$set:state.lastBT},
						runtype:{$set:state.lastRT}
						}
					},
					show:{$set:action.payload}, currentBall:{$set:parseInt(action.event)-1},
					score:{$set:parseInt(state.score)- state.balls[parseInt(action.event)-1].runs},
					wks:{$set:parseInt(state.wks)-state.balls[parseInt(action.event)-1].runtype===3?1:0}
				});
			}
			else
			{
				console.log('ELSE');
				return update(state, 
				{
					balls:{
					[parseInt(action.event)-1]:{	
						balldisplay:{$set:''},
						runs:{$set:0},	
						balltype:{$set:0},
						runtype:{$set:0}
						}
					},
					show:{$set:action.payload}, currentBall:{$set:parseInt(action.event)-1},
					score:{$set:parseInt(state.score)- state.balls[parseInt(action.event)-1].runs},
					wks:{$set:parseInt(state.wks)-state.balls[parseInt(action.event)-1].runtype===3?1:0}
				});
			}
	}

		case BALLTYPE_CHANGE:
		{
			const curBall = state.balls[state.currentBall];
			return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						balltype:{$set:action.payload}	
						}
					}
				});


		/*return {...state, balls:{
					...state.balls,[state.currentBall]:{
						...curBall,
						balltype:action.payload	
						}
					}
				}*/
			}
		case RUNTYPE_CHANGE:
		{
			const curBall = state.balls[state.currentBall];

		return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						runtype:{$set:action.payload}	
						}
					}
				});
	}

		case RUNS_CHANGE:
		{
			const curBall = state.balls[state.currentBall];

		return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						runs:{$set:action.payload}	
						}
					}
				});
		}

		case BALL_CLOSE:
		{
			return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						balldisplay:{$set:''},
						runs:{$set:0},	
						balltype:{$set:0},
						runtype:{$set:0}
						}
					},
					show:{$set:action.payload},
					score:{$set:parseInt(state.score)+ state.balls[state.currentBall].runs},
					wks:{$set:parseInt(state.wks)+state.balls[state.currentBall].runtype===3?1:0}
			});
	}

		case BALL_SET:
		{	
			var ballDisp = "";
			var wks=state.wks;
			var score= state.score;


			const curBall = state.balls[state.currentBall];

			switch(curBall.balltype){
				case "0":
				ballDisp= curBall.runs+"";
				break;

				case "1":
				ballDisp= curBall.runs+"NB";
				break;

				case "2":
				ballDisp= curBall.runs+'WD';
				break;

				default :
				ballDisp= curBall.runs+'';
				break;
			}

			switch(curBall.runtype){
				case "0":
				ballDisp= ballDisp+'';
				break;

				case "1":
				ballDisp= ballDisp+'B';
				break;

				case "2":
				ballDisp= ballDisp+'LB';
				break;

				case "3":
				{
					ballDisp= ballDisp+'WK';
					wks = parseInt(state.wks)+1
				}

				break;

				default :
				ballDisp= ballDisp+'';
				break;
			}

			//return Object.assign({},state, {ballDisplay:ballDisp, show:false});

			console.log('reduce',curBall);
			return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						balldisplay:{$set:ballDisp}	
						}
					},
					score:{$set:parseInt(curBall.runs)+parseInt(state.score)},
					wks:{$set:wks},
					lastBT:{$set:curBall.balltype},
					lastRT:{$set:curBall.runtype},
					LastRun:{$set:curBall.runs}
				,show:{$set:false}});
			//return Object.assign({},state, {show:false ,runs:parseInt(state.runs)+parseInt(state.runsConceeded)});
		}
		default:
		return initialState;
		break;
	}
}
