import React, {Component} from 'react';
import './App.css';
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-balham.css';
import 'tachyons';

import Scroll from './components/Scroll/Scroll'


class App extends Component{
  constructor(props) {
        super(props);

        this.state = {
            columnDefs: [
                {headerName: 'Match Id', field: 'matchid'},
                {headerName: 'Match Name', field: 'matchname'},
                {headerName: 'Team1', field: 'team1'},
                {headerName: 'Team2', field: 'team2'},
                {headerName: 'Overs', field: 'overs'}

            ],
            rowData: []
        }
    }

componentDidMount() {
    fetch('http://localhost:3000', ).then(
      res => res.json()).then(data =>{
         this.state.rowData = data.slice();
         this.setState(this.state);
      })
  }    

   render(){ 
    return (
      <div>
        <h1 className='tc bg-red br4'>{'Scoreboard'}</h1>
        <Scroll>
          <div className="ag-theme-balham pa3"
                style={{height: '600px', width:'1000px', justifyContent:'center'}} >
            <AgGridReact  
                enableSorting={true}
                enableFilter={true}
                columnDefs={this.state.columnDefs}
                rowData={this.state.rowData}>
            </AgGridReact>
          </div>
        </Scroll>
      </div>
  );
}
}

export default App;
