import React from 'react'
import {Button} from 'react-bootstrap';
import './Ball.css';
import  DialogBox from '../components/DialogBox/DialogBox'

const Ball = (props)=>{
	console.log(props);
	const {show, onSetBall, onBallClose, onBallChange, onRTChange, onRunChange, 
            onBallContinue, ballDisplay, onClick, } = this.props;
	return(
		<div className="dib grow br2 pa3 ma2 bw2 shadow-5">	
			<Button type="button" className="btn btn-success btn-circle btn-xl"
			onClick = {onClick}> {ballDisplay}</Button>	
			<DialogBox show = {show} close={onBallClose} ballChange = {onBallChange} rtChange = {onRTChange}
                runChange = {onRunChange} continue = {onBallContinue}/>
		</div>
		);
}

export default Ball;
