import	React from 'react';
import Ball from '../Ball/Ball';

const BallList = ({ balls }) => {
  return (
    <div>
      {
        balls.map((ball, i) => {
          const {show, onSetBall, onBallClose, onBallChange, onRTChange, onRunChange, 
            onBallContinue, ballDisplay} = this.props;
          return (
            <div>
              <Ball
                key={i}
                itemid={balls[i].itemid}
                itemname={balls[i].itemname}
                itemimage={balls[i].itemimage}
                />
              <DialogBox show = {show} close={onBallClose} ballChange = {onBallChange} rtChange = {onRTChange}
                runChange = {onRunChange} continue = {onBallContinue}/>
            </div>
          );
        })
      }
    </div>
  );
}

export default BallList;
