import React from 'react'
import {Button} from 'react-bootstrap';
import './Ball.css';

const Ball = (props)=>{
	return(
		<div className="dib grow br2 pa3 ma2 bw2 shadow-5">	
			<Button type="button" className="btn btn-success btn-circle btn-xl"

			onClick = {props.onClick}> {props.ballDisplay}</Button>				
		</div>
		);
}

export default Ball;
