import	React from 'react';
import Ball from '../Ball/Ball';

const BallList = ({ balls }) => {
  return (
    <div>
      {
        balls.map((ball, i) => {

          return (
            <div>
              <Ball onCLick={this.props.onClick} ballDisplay= {this.props.ballDisplay}/> 
            </div>
          );
        })
      }
    </div>
  );
}

export default BallList;
