import {BALL_CLICK, BALL_SET, BALL_CLOSE, 
	BALLTYPE_CHANGE, RUNTYPE_CHANGE, RUNS_CHANGE,
	REQUEST_BALLS_PENDING,REQUEST_BALLS_SUCCESS,REQUEST_BALLS_FAILED
} from './constants'

import update from 'react-addons-update';


const initialState ={
	isPending : false,
	error : '',
	balls : [],
	show:false,
	score:0,
	wks:0,
	currentBall:0,
	lastRT:0,
	lastBT:0,
	LastRun:0,
	lastBD:'',
	runsOnBall:0,
	wksOnBall:0,
	ballRT:0,
	ballBT:0,
	ballRuns:0,
	ballBD:''
}

export const setBalls=(state =initialState, action = {})=>{
	switch(action.type){
		case REQUEST_BALLS_PENDING:
		return Object.assign({},state, {isPending:true});

		case REQUEST_BALLS_SUCCESS:	 
		return {...state, ispending:false, currentBall:0, balls:action.payload};

		case REQUEST_BALLS_FAILED:	 
		return Object.assign({},state, {isPending:false, error : action.payload});

		case BALL_CLICK:
		return {...state, 
					show:action.payload, currentBall:parseInt(action.event)-1,
					runsOnBall:state.balls[parseInt(action.event)-1].runs,
					wksOnBall:parseInt(state.balls[parseInt(action.event)-1].runtype) === 3?1:0
				}

		case BALLTYPE_CHANGE:
			return {...state, ballBT:action.payload	}

		case RUNTYPE_CHANGE:
		return {...state, ballRT:action.payload }

		case RUNS_CHANGE:
		return {...state, ballRuns:action.payload }

		case BALL_CLOSE:
			return {...state,show:action.payload}

		case BALL_SET:
		{	
			var ballDisp = "";
			var wks=parseInt(state.wks)-parseInt(state.wksOnBall);

			var score= parseInt(state.score)+parseInt(state.ballRuns)-parseInt(state.runsOnBall);

			switch(state.ballBT){
				case "0":
				ballDisp= state.ballRuns+"";
				break;

				case "1":
				ballDisp= state.ballRuns+"NB";
				break;

				case "2":
				ballDisp= state.ballRuns+'WD';
				break;

				default :
				ballDisp= state.ballRuns+'';
				break;
			}

			switch(state.ballRT){
				case "0":
				ballDisp= ballDisp+'';
				break;

				case "1":
				ballDisp= ballDisp+'B';
				break;

				case "2":
				ballDisp= ballDisp+'LB';
				break;

				case "3":
				{
					ballDisp= ballDisp+'WK';
					wks = parseInt(wks)+1
				}

				break;

				default :
				ballDisp= ballDisp+'';
				break;
			}

			return update(state, 
				{
					balls:{
					[state.currentBall]:{	
						balldisplay:{$set:ballDisp},
						balltype:{$set:state.ballBT},
						runtype:{$set:state.ballRT},
						runs:{$set:state.ballRuns}
						}
					},
					score:{$set:score},
					wks:{$set:wks},
					lastBT:{$set:state.ballBT},
					lastRT:{$set:state.ballRT},
					LastRun:{$set:state.ballRuns}
				,show:{$set:false}});
		}
		default:
		return initialState;
		break;
	}
}
